import NextAuth from "next-auth";

declare module "next-auth" {
  interface Session {
    user: {
      id: string;         
      name?: string;
      email?: string;
      image?: string;
      isAdmin: boolean;
      isMasterAdmin: boolean;  
    };
  }

  interface JWT {
    sub?: string;
    name?: string;
    email?: string;
    isAdmin: boolean; 
    isMasterAdmin: boolean;    
  }
}
